public class SwappingTwoNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=2;
		int b=3;
		System.out.println("the swapped numbers are");
			a=a+b;
			b=a-b;
			a=a-b;
	   		System.out.println("swapping a=:"+a);
		System.out.println("swapping b=:"+b);
	}

}
